import { GoogleMap, InfoWindow, LoadScript, Marker } from '@react-google-maps/api';
import React, { useState } from 'react';

const MapComponent = () => {
  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  const defaultCenter = {
    lat: 23.6102, 
    lng: 85.2799  
  };

  const [selectedFPO, setSelectedFPO] = useState(null);

  // Example FPOs with custom icons
  const fposInBlock = [
    {
      name: 'Water Facility',
      position: { lat: 23.605, lng: 85.295 },
      icon: '/icons/water.png' // Path to local image or URL
    },
    {
      name: 'Electricity Facility',
      position: { lat: 23.615, lng: 85.310 },
      icon: '/icons/electricity.png' // Path to local image or URL
    },
    {
      name: 'Agriculture Facility',
      position: { lat: 23.620, lng: 85.320 },
      icon: '/icons/agriculture.png' // Path to local image or URL
    }
  ];

  return (
    <LoadScript googleMapsApiKey="AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk">
      <GoogleMap
        mapContainerStyle={mapStyles}
        zoom={10}
        center={defaultCenter}
      >
        {/* Display FPO markers with custom icons */}
        {fposInBlock.map((fpo, index) => (
          <Marker
            key={index}
            position={fpo.position}
            icon={{
              url: fpo.icon,   // Custom icon path
              scaledSize: new window.google.maps.Size(32, 32)  // Size of the icon
            }}
            onClick={() => setSelectedFPO(fpo)}
          />
        ))}

        {/* Info window for selected FPO */}
        {selectedFPO && (
          <InfoWindow
            position={selectedFPO.position}
            onCloseClick={() => setSelectedFPO(null)}
          >
            <div>
              <h4>{selectedFPO.name}</h4>
              <p>Additional information about this facility.</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default MapComponent;
