import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import {
  Button,
  Col,
  Container,
  Form,
  FormGroup,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Row,
} from "reactstrap";

const SowingForm = () => {
  const [farmerId, setFarmerId] = useState("Orma_00001");
  const [landId, setLandId] = useState("Orma_00001-1");
  const [crop, setCrop] = useState("Tomatoes");
  const [sowingDate, setSowingDate] = useState("2024-08-01");
  const [sownQuantity, setSownQuantity] = useState(500);
  const [isEditable, setIsEditable] = useState(true);

  const [modalOpen, setModalOpen] = useState(false); // Modal state
  const [submittedData, setSubmittedData] = useState(null); // Store submitted data

  const handleSubmit = (e) => {
    e.preventDefault();

    if (sownQuantity <= 0) {
      alert("Sown Quantity must be greater than zero");
      return;
    }

    if (!farmerId || !landId || !crop || !sowingDate) {
      alert("All fields are required");
      return;
    }

    const formData = {
      farmerId,
      landId,
      crop,
      sowingDate,
      sownQuantity,
    };

    setSubmittedData(formData); // Store the submitted form data
    setModalOpen(true); // Open modal
  };

  const handleEdit = () => {
    setIsEditable(!isEditable);
  };

  const handleCancel = () => {
    setFarmerId("Orma_00001");
    setLandId("Orma_00001-1");
    setCrop("Tomatoes");
    setSowingDate("2024-08-01");
    setSownQuantity(500);
  };

  const toggleModal = () => setModalOpen(!modalOpen); // Toggle modal visibility

  return (
    <Container fluid className="bg-light min-vh-100 d-flex justify-content-center align-items-center">
      <Row className="shadow p-5 bg-white rounded" style={{ maxWidth: "500px", width: "100%" }}>
        <Col>
          <h3 className="text-center mb-4">Sowing Information</h3>
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label for="farmerId">Farmer ID</Label>
              <Input
                type="select"
                id="farmerId"
                value={farmerId}
                onChange={(e) => setFarmerId(e.target.value)}
                disabled={!isEditable}
              >
                <option value="Orma_00001">Orma_00001</option>
                <option value="Orma_00002">Orma_00002</option>
              </Input>
            </FormGroup>

            <FormGroup>
              <Label for="landId">Land ID</Label>
              <Input
                type="select"
                id="landId"
                value={landId}
                onChange={(e) => setLandId(e.target.value)}
                disabled={!isEditable}
              >
                <option value="Orma_00001-1">Orma_00001-1</option>
                <option value="Orma_00001-2">Orma_00001-2</option>
              </Input>
            </FormGroup>

            <FormGroup>
              <Label for="crop">Crop</Label>
              <Input
                type="select"
                id="crop"
                value={crop}
                onChange={(e) => setCrop(e.target.value)}
                disabled={!isEditable}
              >
                <option value="Tomatoes">Tomatoes</option>
                <option value="Corn">Corn</option>
                <option value="Wheat">Wheat</option>
              </Input>
            </FormGroup>

            <FormGroup>
              <Label for="sowingDate">Sowing Date</Label>
              <Input
                type="date"
                id="sowingDate"
                value={sowingDate}
                onChange={(e) => setSowingDate(e.target.value)}
                disabled={!isEditable}
              />
            </FormGroup>

            <FormGroup>
              <Label for="sownQuantity">Sown Quantity (kg)</Label>
              <Input
                type="number"
                id="sownQuantity"
                value={sownQuantity}
                onChange={(e) => setSownQuantity(e.target.value)}
                disabled={!isEditable}
              />
            </FormGroup>

            <div className="d-flex justify-content-between">
              <Button type="button" color="primary" onClick={handleEdit}>
                {isEditable ? "Lock Form" : "Edit"}
              </Button>
              <Button type="submit" color="success" disabled={!isEditable}>
                Submit
              </Button>
              <Button type="button" color="danger" onClick={handleCancel}>
                Cancel
              </Button>
            </div>
          </Form>
        </Col>
      </Row>

      {/* Modal for showing confirmation on submission */}
      <Modal isOpen={modalOpen} toggle={toggleModal}>
        <ModalHeader toggle={toggleModal}>Form Submitted Successfully!</ModalHeader>
        <ModalBody>
          <p>Thank you for submitting the form. Here is the information you entered:</p>
          {submittedData && (
            <ul>
              <li><strong>Farmer ID:</strong> {submittedData.farmerId}</li>
              <li><strong>Land ID:</strong> {submittedData.landId}</li>
              <li><strong>Crop:</strong> {submittedData.crop}</li>
              <li><strong>Sowing Date:</strong> {submittedData.sowingDate}</li>
              <li><strong>Sown Quantity:</strong> {submittedData.sownQuantity} kg</li>
            </ul>
          )}
        </ModalBody>
        <ModalFooter>
          <Button color="secondary" onClick={toggleModal}>
            Close
          </Button>
        </ModalFooter>
      </Modal>
    </Container>
  );
};

export default SowingForm;
