import { Auth0Provider, useAuth0 } from '@auth0/auth0-react';
import { Autocomplete, GoogleMap, InfoWindow, LoadScript, Marker } from "@react-google-maps/api";
import React, { useRef, useState } from 'react';
import { Navigate, Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';

 // Adjust the path if necessary
import About from './components/About';
import Contact from './components/Contact';
import Header from './components/Header';
import Home from './components/Home';
import MyMapComponent from './components/MyMapComponent';
import Profile from './components/Profile';
import Sidebar from './components/Sidebar';

// Move the libraries array outside the component
const libraries = ['places'];

function App() {
  const { isAuthenticated, loginWithRedirect, logout } = useAuth0();

  const [farm, setFarm] = useState(null);
  const [id, setId] = useState('');
  const [showMap, setShowMap] = useState(false);
  const [latitude, setLatitude] = useState(-34.397);
  const [longitude, setLongitude] = useState(150.644);
  const [placeName, setPlaceName] = useState('');  // For place name search
  const [autocomplete, setAutocomplete] = useState(null);  // For Autocomplete instance

  const autocompleteRef = useRef(null);  // Reference to the input field

  // Fetch farm data by ID
  const fetchFarmData = () => {
    fetch(`http://localhost:8083/farmntech/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => response.json())
      .then(data => {
        setFarm(data);
      })
      .catch(error => console.error("Error fetching farm data:", error));
  };

  // Handle place search using Google Autocomplete
  const onPlaceChanged = () => {
    if (autocomplete !== null) {
      const place = autocomplete.getPlace();
      if (place.geometry) {
        setLatitude(place.geometry.location.lat());
        setLongitude(place.geometry.location.lng());
        setPlaceName(place.name);  // Store the selected place name
        setShowMap(true);  // Show the map after search
      }
    } else {
      console.log("Autocomplete is not loaded yet!");
    }
  };

  const handleIdChange = (e) => setId(e.target.value);
  const handleIdSubmit = (e) => {
    e.preventDefault();
    fetchFarmData();
  };

  const mapContainerStyle = {
    height: "400px",
    width: "800px"
  };

  const handleMapClick = () => setShowMap(true);
  const handleHideMapClick = () => setShowMap(false);
  const handleLatitudeChange = (e) => setLatitude(parseFloat(e.target.value));
  const handleLongitudeChange = (e) => setLongitude(parseFloat(e.target.value));

  return (
    <div>
      <Router>
        <Header />
        <Routes>
          <Route path="/sidebar" element={<Sidebar />} />
          <Route path="/home" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/map" element={<MyMapComponent />} />
          <Route path="/profile" element={isAuthenticated ? <Profile /> : <Navigate to="/home" />} />
          <Route path="/" element={<Home />} />
        </Routes>
      </Router>

      {!isAuthenticated ? (
        <button onClick={loginWithRedirect}>Login</button>
      ) : (
        <>
          <form onSubmit={handleIdSubmit}>
            <label>Enter Farm ID: </label>
            <input 
              type="text" 
              value={id} 
              onChange={handleIdChange}
            />
            <button type="submit">Fetch Farm Data</button>
          </form>

          {/* Display fetched farm data */}
          {farm && (
            <div className='farm'>
              <h2>Farm Details</h2>
              <p>Farm No: {farm.f_no}</p>
              <p>Address: {farm.address}</p>
              <p>Quantity: {farm.qnt}</p>
            </div>
          )}

          <LoadScript
            googleMapsApiKey="AIzaSyD0KaEX-bXcnOS7XFfLKnVyiet_Kfi-8xM"
            libraries={libraries}  // Use the static array
          >
            {/* Location Autocomplete */}
            <div className='location-search'>
              <Autocomplete
                onLoad={(autoC) => setAutocomplete(autoC)}
                onPlaceChanged={onPlaceChanged}
              >
                <input
                  type="text"
                  placeholder="Search for a place"
                  style={{ width: "300px", height: "40px", fontSize: "16px" }}
                  ref={autocompleteRef}
                />
              </Autocomplete>
            </div>

            <div className='map'>
              <label>Latitude: </label>
              <input type="number" value={latitude} onChange={handleLatitudeChange} />
              <label>Longitude: </label>
              <input type="number" value={longitude} onChange={handleLongitudeChange} />
            </div>

            <button onClick={handleMapClick}>Show Map</button>
            <button onClick={handleHideMapClick}>Hide Map</button>
            <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>

            {showMap && (
              <GoogleMap
                mapContainerStyle={mapContainerStyle}
                center={{ lat: latitude, lng: longitude }}
                zoom={14}
              >
                {/* Using AdvancedMarkerElement instead of google.maps.Marker */}
                <Marker
                  position={{ lat: latitude, lng: longitude }}
                />
                <InfoWindow position={{ lat: latitude, lng: longitude }}>
                  <div>{placeName ? `Selected Place: ${placeName}` : "Map Location"}</div>
                </InfoWindow>
              </GoogleMap>
            )}
          </LoadScript>
        </>
      )}
    </div>
  );
}

// Wrap App component with Auth0Provider
const Root = () => (
  <Auth0Provider
    domain="dev-0va1gvwgwfk0n27p.us.auth0.com"
    clientId="G4oOtJbNvzl9dn8c4bSdoa5EdZsxoOIk"
    authorizationParams={{
      redirect_uri: window.location.origin
    }}
  >
    <App />
  </Auth0Provider>
);

export default Root;
