import { GoogleMap, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Center the map at a point in Jharkhand
const center = {
  lat: 23.3441,
  lng: 85.3096
};

// Polygon coordinates for Ranchi District
const ranchiDistrict = [
  { lat: 23.452592, lng: 85.450852 },
  { lat: 23.452592, lng: 85.470852 },
  { lat: 23.432592, lng: 85.470852 },
  { lat: 23.432592, lng: 85.450852 },
];

// Polygon coordinates for blocks within Ranchi
const oramanjhiBlock = [
  { lat: 23.442592, lng: 85.460852 }, // North-West
  { lat: 23.442592, lng: 85.480852 }, // North-East
  { lat: 23.422592, lng: 85.480852 }, // South-East
  { lat: 23.422592, lng: 85.460852 }, // South-West
];

// Coordinates for the smaller polygon representing Casa Blanca FPO
const casaBlancaPolygon = [
  { lat: 23.442592, lng: 85.461852 }, // North-West
  { lat: 23.442592, lng: 85.463852 }, // North-East
  { lat: 23.441592, lng: 85.463852 }, // South-East
  { lat: 23.441592, lng: 85.461852 }, // South-West
];

// Calculate the center for the polygon's label (approximation)
const casaBlancaCenter = {
  lat: (23.442592 + 23.441592) / 2,
  lng: (85.461852 + 85.463852) / 2
};

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk"
  });

  const [selectedDistrict, setSelectedDistrict] = useState(null);

  const handleDistrictClick = (district) => {
    setSelectedDistrict(district);
  };

  const handleBlockClick = (block) => {
    console.log(`${block} block clicked`);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={10}
    >
      {/* Render district polygons when no district is selected */}
      {!selectedDistrict && (
        <>
          {/* Ranchi Polygon */}
          <PolygonF
            paths={ranchiDistrict}
            options={{ fillColor: 'rgba(0, 255, 0, 0.5)', strokeColor: 'green' }}
            onClick={() => handleDistrictClick('Ranchi')}
          />
        </>
      )}

      {/* Render block polygons when Ranchi district is selected */}
      {selectedDistrict === 'Ranchi' && (
        <>
          {/* Oramanjhi Block Polygon */}
          <PolygonF
            paths={oramanjhiBlock}
            options={{ fillColor: 'rgba(255, 255, 0, 0.5)', strokeColor: 'yellow' }}
            onClick={() => handleBlockClick('Oramanjhi')}
          />

          {/* Casa Blanca Polygon */}
          <PolygonF
            paths={casaBlancaPolygon}
            options={{
              fillColor: 'rgba(255, 0, 0, 0.5)', // Light red fill for Casa Blanca FPO
              strokeColor: 'red',                // Red border for Casa Blanca FPO
              strokeWeight: 2
            }}
          />

          {/* Simulated label using a marker at the center of the polygon */}
          {isLoaded && window.google && (
            <MarkerF
              position={casaBlancaCenter}
              label={{
                text: "Casa Blanca FPO",  // Label text
                color: 'black',           // Label color
                fontWeight: 'bold',       // Font weight of the label
                fontSize: '14px'          // Font size
              }}
              options={{
                icon: { path: window.google.maps.SymbolPath.CIRCLE, scale: 0 } // Hide default marker icon
              }}
            />
          )}
        </>
      )}
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
