import { GoogleMap, InfoWindow, LoadScript, Marker, Polygon } from '@react-google-maps/api';
import React, { useState } from 'react';

const MapComponent = () => {
  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  const defaultCenter = {
    lat: 23.6102, // Jharkhand's latitude
    lng: 85.2799  // Jharkhand's longitude
  };

  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedBlock, setSelectedBlock] = useState(null);
  const [selectedFPO, setSelectedFPO] = useState(null);  // Track selected FPO for info window

  // Define district polygons
  const districtPolygons = [
    {
      name: 'Ranchi',
      path: [ /* Array of coordinates for Ranchi */ ],
      color: '#FF0000' // Red for Ranchi
    },
    {
      name: 'Dhanbad',
      path: [ /* Array of coordinates for Dhanbad */ ],
      color: '#00FF00' // Green for Dhanbad
    }
    // Add other districts similarly
  ];

  // Define blocks for each district
  const blocksInDistrict = {
    'Ranchi': [
      { name: 'Block 1', path: [ /* Coordinates */ ], color: '#FFD700' },
      { name: 'Block 2', path: [ /* Coordinates */ ], color: '#ADFF2F' },
    ],
    'Dhanbad': [
      { name: 'Block 1', path: [ /* Coordinates */ ], color: '#FF4500' },
      { name: 'Block 2', path: [ /* Coordinates */ ], color: '#1E90FF' },
    ]
  };

  // Define FPOs and their sowing data
  const fposInBlock = {
    'Block 1': [
      { 
        name: 'FPO 1', 
        position: { lat: 23.456, lng: 85.234 },
        productionData: {
          tomato: 500,  // Sowing data for tomato (in tonnes, for example)
          potato: 300,
          strawberry: 100
        }
      },
      { 
        name: 'FPO 2', 
        position: { lat: 23.467, lng: 85.245 },
        productionData: {
          tomato: 200,
          potato: 400,
          strawberry: 50
        }
      }
    ],
    'Block 2': [
      { 
        name: 'FPO 3', 
        position: { lat: 23.456, lng: 85.256 },
        productionData: {
          tomato: 600,
          potato: 200,
          strawberry: 70
        }
      },
      { 
        name: 'FPO 4', 
        position: { lat: 23.478, lng: 85.278 },
        productionData: {
          tomato: 150,
          potato: 500,
          strawberry: 80
        }
      }
    ]
    // Define FPOs for other blocks
  };

  return (
    <LoadScript googleMapsApiKey="AIzaSyD0KaEX-bXcnOS7XFfLKnVyiet_Kfi-8xM">
      <GoogleMap
        mapContainerStyle={mapStyles}
        zoom={7}
        center={defaultCenter}
      >
        {/* Display district polygons */}
        {districtPolygons.map((district, index) => (
          <Polygon
            key={index}
            paths={district.path}
            options={{
              fillColor: district.color,
              fillOpacity: 0.4,
              strokeColor: district.color,
              strokeOpacity: 0.8,
              strokeWeight: 2,
            }}
            onClick={() => {
              setSelectedDistrict(district.name);
              setSelectedBlock(null); // Reset block selection when switching district
              setSelectedFPO(null);   // Reset FPO selection
            }}
          />
        ))}

        {/* Display blocks if a district is selected */}
        {selectedDistrict && blocksInDistrict[selectedDistrict].map((block, index) => (
          <Polygon
            key={index}
            paths={block.path}
            options={{
              fillColor: block.color,
              fillOpacity: 0.5,
              strokeColor: block.color,
              strokeOpacity: 0.8,
              strokeWeight: 2,
            }}
            onClick={() => setSelectedBlock(block.name)}  // Set clicked block
          />
        ))}

        {/* Display FPOs if a block is selected */}
        {selectedBlock && fposInBlock[selectedBlock].map((fpo, index) => (
          <Marker
            key={index}
            position={fpo.position}
            label={fpo.name}
            onClick={() => setSelectedFPO(fpo)}  // Set clicked FPO for InfoWindow
          />
        ))}

        {/* Display InfoWindow for selected FPO */}
        {selectedFPO && (
          <InfoWindow
            position={selectedFPO.position}
            onCloseClick={() => setSelectedFPO(null)}
          >
            <div>
              <h4>{selectedFPO.name}</h4>
              <p>Tomato: {selectedFPO.productionData.tomato} tonnes</p>
              <p>Potato: {selectedFPO.productionData.potato} tonnes</p>
              <p>Strawberry: {selectedFPO.productionData.strawberry} tonnes</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default MapComponent;
