import { GoogleMap, InfoWindowF, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React from 'react';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Coordinates for Casa Blanca Estate, Rukka, Ranchi, Jharkhand
const casaBlanca = {
  lat: 23.4556,
  lng: 85.4539
};

// Polygon coordinates to surround Casa Blanca Estate
const casaBlancaPolygonCoords = [
  { lat: 23.4600, lng: 85.4500 },
  { lat: 23.4600, lng: 85.4570 },
  { lat: 23.4530, lng: 85.4570 },
  { lat: 23.4530, lng: 85.4500 }
];

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk"
  });

  const [map, setMap] = React.useState(null);
  const [selectedMarker, setSelectedMarker] = React.useState(false); // State to manage the InfoWindow

  const onLoad = React.useCallback((map) => {
    map.setZoom(15); // Zoom level closer to Casa Blanca Estate
    setMap(map);
  }, []);

  const onUnmount = React.useCallback((map) => {
    setMap(null);
  }, []);

  const handleMarkerClick = () => {
    setSelectedMarker(true);
  };

  const handleInfoWindowClose = () => {
    setSelectedMarker(false);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={casaBlanca}
      zoom={15}
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      {/* Marker at Casa Blanca Estate */}
      <MarkerF
        position={casaBlanca}
        onClick={handleMarkerClick}
      />

      {/* Polygon surrounding Casa Blanca Estate */}
      <PolygonF
        paths={casaBlancaPolygonCoords}
        options={{
          fillColor: 'rgba(0, 0, 255, 0.2)', // Light blue fill
          strokeColor: 'blue',
          strokeOpacity: 1,
          strokeWeight: 2,
        }}
      />

      {/* InfoWindow for Casa Blanca Estate */}
      {selectedMarker && (
        <InfoWindowF
          position={casaBlanca}
          onCloseClick={handleInfoWindowClose}
        >
          <div>
            <h4>Casa Blanca Estate</h4>
            <p>Description or additional information about Casa Blanca Estate.</p>
          </div>
        </InfoWindowF>
      )}

      <></>
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
