import { GoogleMap, InfoWindowF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px'
};

// Center the map on Ranchi
const center = {
  lat: 23.3441,
  lng: 85.3096
};

// Expanded coordinates for the Ranchi district
const ranchiDistrict = [
  { lat: 23.5000, lng: 85.2000 }, // North-West
  { lat: 23.5000, lng: 85.4000 }, // North-East
  { lat: 23.2000, lng: 85.4000 }, // South-East
  { lat: 23.2000, lng: 85.2000 }, // South-West
];

// Subdistrict polygons inside Ranchi
const subDistrict1 = [
  { lat: 23.4500, lng: 85.2500 },
  { lat: 23.4500, lng: 85.3200 },
  { lat: 23.4000, lng: 85.3200 },
  { lat: 23.4000, lng: 85.2500 },
];

const subDistrict2 = [
  { lat: 23.3500, lng: 85.3000 },
  { lat: 23.3500, lng: 85.3500 },
  { lat: 23.3000, lng: 85.3500 },
  { lat: 23.3000, lng: 85.3000 },
];

// Casa Blanca FPO
const casaBlancaPolygon = [
  { lat: 23.442592, lng: 85.460852 },
  { lat: 23.442592, lng: 85.462852 },
  { lat: 23.441592, lng: 85.462852 },
  { lat: 23.441592, lng: 85.460852 },
];

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk"
  });

  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedFPO, setSelectedFPO] = useState(false);

  const handlePolygonClick = (district) => {
    setSelectedDistrict(district);
  };

  const handleFPOClick = () => {
    setSelectedFPO(true);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={10}
    >
      {/* Polygon for Ranchi district */}
      <PolygonF
        paths={ranchiDistrict}
        options={{
          fillColor: 'lightblue',
          strokeColor: 'blue',
          strokeOpacity: 1,
          strokeWeight: 2,
        }}
        onClick={() => handlePolygonClick('Ranchi')}
      />

      {/* Subdistrict polygons (e.g., Ormanjhi) */}
      {selectedDistrict === 'Ranchi' && (
        <>
          <PolygonF
            paths={subDistrict1}
            options={{
              fillColor: 'lightgreen',
              strokeColor: 'green',
              strokeOpacity: 1,
              strokeWeight: 2,
            }}
            onClick={handleFPOClick}
          />
          <PolygonF
            paths={subDistrict2}
            options={{
              fillColor: 'lightyellow',
              strokeColor: 'orange',
              strokeOpacity: 1,
              strokeWeight: 2,
            }}
            onClick={handleFPOClick}
          />
        </>
      )}

      {/* Casa Blanca FPO with label */}
      {selectedFPO && (
        <PolygonF
          paths={casaBlancaPolygon}
          options={{
            fillColor: 'darkred',
            strokeColor: 'red',
            strokeOpacity: 1,
            strokeWeight: 2,
          }}
        />
      )}
      {selectedFPO && (
        <InfoWindowF position={{ lat: 23.442592, lng: 85.460852 }}>
          <div>
            <h4>This is our Casa Blanca FPO</h4>
          </div>
        </InfoWindowF>
      )}
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
