import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to Ormanji coordinates
const center = {
  lat: 23.3680, // Latitude for Ormanji
  lng: 85.3200, // Longitude for Ormanji
};

// Coordinates for the polygon covering Jharkhand
const jharkhandCoords = [
  { lat: 24.4001, lng: 83.9739 }, // Northwest
  { lat: 24.2087, lng: 84.1255 }, // Near Gaya
  { lat: 24.0171, lng: 84.1621 }, // Near Ranchi
  { lat: 23.8744, lng: 85.1413 }, // Near Hazaribagh
  { lat: 23.7008, lng: 85.5683 }, // Near Dhanbad
  { lat: 23.5576, lng: 85.7832 }, // Near Bokaro
  { lat: 23.4221, lng: 86.0507 }, // Near Purulia
  { lat: 23.3680, lng: 86.1864 }, // South of Jharkhand
  { lat: 23.4221, lng: 86.3087 }, // Near East Singhbhum
  { lat: 23.5784, lng: 86.1278 }, // East of Jamshedpur
  { lat: 23.6268, lng: 86.0389 }, // Close to Odisha border
  { lat: 24.1886, lng: 85.2771 }, // North near Hazaribagh
  { lat: 24.4001, lng: 83.9739 }, // Close the polygon
];

// Coordinates for Ormanji
const ormanjiCoords = [
  { lat: 23.3680, lng: 85.3200 },
  { lat: 23.3720, lng: 85.3200 },
  { lat: 23.3700, lng: 85.3250 },
  { lat: 23.3680, lng: 85.3250 },
  { lat: 23.3680, lng: 85.3200 }, // Close the polygon
];

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your Google Maps API key
  });

  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);

  const handleJharkhandPolygonClick = () => {
    setShowJharkhandPolygon(false);
  };

  return isLoaded ? (
    <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={7}>
      {/* Polygon covering Jharkhand */}
      {showJharkhandPolygon && (
        <PolygonF
          paths={jharkhandCoords}
          options={{
            fillColor: '#FF0000', // Fill color for Jharkhand polygon
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={handleJharkhandPolygonClick} // Click event to hide the Jharkhand polygon
        />
      )}

      {/* Polygon covering Ormanji */}
      <PolygonF
        paths={ormanjiCoords}
        options={{
          fillColor: '#00FF00', // Fill color for Ormanji polygon
          strokeColor: '#000000',
          strokeWeight: 2,
          fillOpacity: 0.6,
        }}
      />
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
