import { GoogleMap, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Center the map on Ranchi
const center = {
  lat: 23.3441,
  lng: 85.3096
};

// Coordinates for the Ranchi district
const ranchiDistrict = [
  { lat: 23.452592, lng: 85.450852 }, // North-West
  { lat: 23.452592, lng: 85.470852 }, // North-East
  { lat: 23.432592, lng: 85.470852 }, // South-East
  { lat: 23.432592, lng: 85.450852 }, // South-West
];

// Coordinates for Ormanjhi block
const oramanjhiBlock = [
  { lat: 23.442592, lng: 85.460852 }, // North-West
  { lat: 23.442592, lng: 85.480852 }, // North-East
  { lat: 23.422592, lng: 85.480852 }, // South-East
  { lat: 23.422592, lng: 85.460852 }, // South-West
];

// Coordinates for Casa Blanca FPO polygon
const casaBlancaPolygon = [
  { lat: 23.442592, lng: 85.461852 }, // North-West
  { lat: 23.442592, lng: 85.463852 }, // North-East
  { lat: 23.441592, lng: 85.463852 }, // South-East
  { lat: 23.441592, lng: 85.461852 }, // South-West
];

// Center of the Casa Blanca polygon for the label
const casaBlancaCenter = {
  lat: (23.442592 + 23.441592) / 2,
  lng: (85.461852 + 85.463852) / 2
};

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk"
  });

  const [selectedDistrict, setSelectedDistrict] = useState(null);

  const handleDistrictClick = (district) => {
    setSelectedDistrict(district);
  };

  const handleBlockClick = (block) => {
    console.log(`${block} block clicked`);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={10}
    >
      {/* Show Ranchi district when no specific block is selected */}
      {!selectedDistrict && (
        <>
          {/* Ranchi district polygon with a dark color */}
          <PolygonF
            paths={ranchiDistrict}
            options={{
              fillColor: 'rgba(0, 0, 128, 0.6)', // Dark blue fill
              strokeColor: 'navy',               // Darker border
              strokeWeight: 2
            }}
            onClick={() => handleDistrictClick('Ranchi')}
          />
        </>
      )}

      {/* Show blocks inside Ranchi when Ranchi is selected */}
      {selectedDistrict === 'Ranchi' && (
        <>
          {/* Ormanjhi block polygon with a different dark color */}
          <PolygonF
            paths={oramanjhiBlock}
            options={{
              fillColor: 'rgba(128, 0, 0, 0.6)', // Dark red fill
              strokeColor: 'darkred',            // Dark red border
              strokeWeight: 2
            }}
            onClick={() => handleBlockClick('Ormanjhi')}
          />

          {/* Casa Blanca FPO polygon with a dark color */}
          <PolygonF
            paths={casaBlancaPolygon}
            options={{
              fillColor: 'rgba(0, 100, 0, 0.6)', // Dark green fill for Casa Blanca
              strokeColor: 'darkgreen',          // Dark green border for Casa Blanca
              strokeWeight: 2
            }}
          />

          {/* Label for Casa Blanca */}
          {isLoaded && window.google && (
            <MarkerF
              position={casaBlancaCenter}
              label={{
                text: "Casa Blanca FPO",  // Label text
                color: 'black',           // Label color
                fontWeight: 'bold',       // Font weight
                fontSize: '14px'          // Font size
              }}
              options={{
                icon: { path: window.google.maps.SymbolPath.CIRCLE, scale: 0 } // Hide marker icon
              }}
            />
          )}
        </>
      )}
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
