import { GoogleMap, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React from 'react';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Center the map at the new coordinates 23.442592425229485, 85.4608527
const center = {
  lat: 23.442592425229485,
  lng: 85.4608527
};

// Define the coordinates for the polygon surrounding the center
const polygonCoords = [
  { lat: 23.452592, lng: 85.450852 }, // North-West
  { lat: 23.452592, lng: 85.470852 }, // North-East
  { lat: 23.432592, lng: 85.470852 }, // South-East
  { lat: 23.432592, lng: 85.450852 }, // South-West
];

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk"
  });

  const [map, setMap] = React.useState(null);

  const onLoad = React.useCallback((map) => {
    map.setZoom(14);  // Adjust zoom level for better view
    setMap(map);
  }, []);

  const onUnmount = React.useCallback((map) => {
    setMap(null);
  }, []);

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={14} // Adjust zoom level
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      {/* Marker at the center location */}
      <MarkerF
        position={center}
      />

      {/* Polygon surrounding the center */}
      <PolygonF
        paths={polygonCoords}
        options={{
          fillColor: 'rgba(0, 255, 0, 0.3)', // Light green fill
          strokeColor: 'green', // Green border
          strokeOpacity: 1,     // Fully opaque border
          strokeWeight: 2,      // Border thickness
        }}
      />

      { /* You can add more child components, such as markers, info windows, etc. */ }
      <></>
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
