import { GoogleMap, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

const center = {
  lat: 23.3441,
  lng: 85.3096,
};

// Define coordinates for a single polygon (Ormanji)
const ormanjiCoords = [
  { lat: 23.460, lng: 85.260 },
  { lat: 23.470, lng: 85.260 },
  { lat: 23.465, lng: 85.270 },
  { lat: 23.460, lng: 85.270 },
  { lat: 23.460, lng: 85.290 },
  { lat: 23.470, lng: 85.290 },
  { lat: 23.475, lng: 85.300 },
  { lat: 23.460, lng: 85.300 },
];

const iconCoordinate = { lat: 23.465, lng: 85.285 };

// Material Icon name for the polygon
const iconLabel = "location_on"; // Change to your preferred Material Icon

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your Google Maps API key
  });

  return isLoaded ? (
    <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={15}>
      {/* Single Polygon for Ormanji */}
      <PolygonF
        paths={ormanjiCoords}
        options={{
          fillColor: '#FF0000', // Fill color for the polygon
          strokeColor: '#000000',
          strokeWeight: 2,
          fillOpacity: 0.6,
        }}
      />

      {/* Icon for Ormanji */}
      <MarkerF
        position={iconCoordinate}
        icon={{
          path: window.google.maps.SymbolPath.CIRCLE, // Hide default marker
          scale: 0,
        }}
      >
        <div style={{
          position: 'absolute',
          transform: 'translate(-50%, -50%)',
          fontSize: '24px',
          color: 'black',
        }}>
          <span className="material-icons">{iconLabel}</span>
        </div>
      </MarkerF>
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
