import { GoogleMap, InfoWindowF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Centering map in Jharkhand
const center = {
  lat: 23.6102,
  lng: 85.2799
};

// Define the polygons for districts (Ranchi and Dhanbad)
const ranchiPolygonCoords = [
  { lat: 23.42, lng: 85.31 },
  { lat: 23.48, lng: 85.45 },
  { lat: 23.58, lng: 85.35 },
  { lat: 23.52, lng: 85.20 },
];

const dhanbadPolygonCoords = [
  { lat: 23.80, lng: 86.30 },
  { lat: 23.85, lng: 86.45 },
  { lat: 23.90, lng: 86.35 },
  { lat: 23.82, lng: 86.20 },
];

// Blocks within Ranchi district
const ranchiBlocksCoords = [
  // Example blocks of Ranchi (Ormanjhi and others)
  { name: 'Ormanjhi', coords: [{ lat: 23.45, lng: 85.38 }, { lat: 23.48, lng: 85.40 }, { lat: 23.47, lng: 85.35 }, { lat: 23.45, lng: 85.34 }] },
  { name: 'Block 2', coords: [{ lat: 23.50, lng: 85.36 }, { lat: 23.53, lng: 85.38 }, { lat: 23.52, lng: 85.34 }, { lat: 23.50, lng: 85.33 }] },
];

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY"  // replace with your key
  });

  const [map, setMap] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedBlock, setSelectedBlock] = useState(null);
  const [showInfoWindow, setShowInfoWindow] = useState(null);

  const onLoad = (map) => {
    map.setZoom(8); // Default zoom for viewing the whole state
    setMap(map);
  };

  const onUnmount = (map) => {
    setMap(null);
  };

  // Function to handle district click
  const handleDistrictClick = (district) => {
    setSelectedDistrict(district);
    map.panTo(center); // Adjust this to pan to clicked district's center
    map.setZoom(10);  // Zoom in to the clicked district
  };

  // Function to handle block click within Ranchi
  const handleBlockClick = (block) => {
    setSelectedBlock(block);
    setShowInfoWindow(true); // Show info window on block click
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={8}
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      {/* Ranchi district polygon */}
      <PolygonF
        paths={ranchiPolygonCoords}
        options={{
          fillColor: 'rgba(255, 0, 0, 0.3)',  // Red fill for Ranchi
          strokeColor: 'red',
          strokeWeight: 2,
        }}
        onClick={() => handleDistrictClick('Ranchi')}
      />

      {/* Dhanbad district polygon */}
      <PolygonF
        paths={dhanbadPolygonCoords}
        options={{
          fillColor: 'rgba(0, 0, 255, 0.3)',  // Blue fill for Dhanbad
          strokeColor: 'blue',
          strokeWeight: 2,
        }}
        onClick={() => handleDistrictClick('Dhanbad')}
      />

      {/* Show blocks if Ranchi is selected */}
      {selectedDistrict === 'Ranchi' && ranchiBlocksCoords.map((block, index) => (
        <PolygonF
          key={index}
          paths={block.coords}
          options={{
            fillColor: 'rgba(0, 255, 0, 0.3)', // Green fill for blocks
            strokeColor: 'green',
            strokeWeight: 2,
          }}
          onClick={() => handleBlockClick(block.name)}
        />
      ))}

      {/* Show InfoWindow when a block is clicked */}
      {showInfoWindow && selectedBlock && (
        <InfoWindowF
          position={ranchiBlocksCoords[0].coords[0]}  // Position info window at the first coordinate of block
          onCloseClick={() => setShowInfoWindow(false)}
        >
          <div>
            <h4>{selectedBlock}</h4>
            <p>FPO: Example FPO (Oramanjhi)</p>
            <p>Details about the block and its FPOs go here.</p>
          </div>
        </InfoWindowF>
      )}
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
