import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to India level
const center = {
  lat: 20.5937,  // Latitude of India
  lng: 78.9629,  // Longitude of India
};

const zoomLevel = 5;  // Adjusted zoom level for the India view

// Coordinates for the Jharkhand polygon
const jharkhandCoords = [
  { lat: 24.3011, lng: 84.4983 }, // 24°18'04"N 84°29'54"E
  { lat: 24.4260, lng: 84.3156 }, // 24°25'37"N 84°18'56"E
  { lat: 24.5617, lng: 84.2892 }, // 24°33'42"N 84°17'21"E
  { lat: 24.5683, lng: 84.1817 }, // 24°34'06"N 84°10'54"E
  { lat: 24.5303, lng: 84.0994 }, // 24°31'49"N 84°05'58"E
  { lat: 24.6567, lng: 83.9983 }, // 24°39'24"N 83°59'54"E
  { lat: 24.5661, lng: 83.9333 }, // 24°33'58"N 83°56'00"E
  { lat: 24.5497, lng: 83.8011 }, // 24°32'59"N 83°48'04"E
  { lat: 24.5467, lng: 83.5272 }, // 24°32'48"N 83°31'38"E
  { lat: 24.5225, lng: 83.3947 }, // 24°31'21"N 83°23'41"E
  { lat: 24.4186, lng: 85.0219 }, // 24°25'07"N 85°01'19"E
  { lat: 24.4003, lng: 84.8878 }, // 24°24'01"N 84°53'16"E
  { lat: 24.4262, lng: 85.0422 }, // 24°25'34"N 85°02'... (trimmed for brevity)
];

// Map Component
const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk",
  });

  const [map, setMap] = useState(null);

  const handlePolygonClick = (polygonCoords, zoomLevel) => {
    const bounds = new window.google.maps.LatLngBounds();
    polygonCoords.forEach(coord => bounds.extend(coord));
    const polygonCenter = bounds.getCenter();

    map.setCenter(polygonCenter);
    map.setZoom(zoomLevel);
  };

  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={zoomLevel}
    >
      {showJharkhandPolygon && (
        <PolygonF
          paths={jharkhandCoords}
          options={{ fillColor: '#00FF00', strokeColor: '#000000' }}
          onClick={() => handlePolygonClick(jharkhandCoords, 8)}
        />
      )}
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
