import { GoogleMap, InfoWindowF, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';
import logo from './img/logo.jpeg';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center the map on Jharkhand
const center = {
  lat: 23.3441,
  lng: 85.3096,
};

// Adjusted coordinates for districts to make them irregular in shape
const districts = {
  ranchi: [
    { lat: 23.460, lng: 85.330 },
    { lat: 23.470, lng: 85.370 },
    { lat: 23.440, lng: 85.380 },
    { lat: 23.430, lng: 85.340 },
  ],
  dhanbad: [
    { lat: 23.460, lng: 85.360 },
    { lat: 23.490, lng: 85.400 },
    { lat: 23.440, lng: 85.410 },
    { lat: 23.430, lng: 85.360 },
  ],
  jamshedpur: [
    { lat: 23.420, lng: 85.360 },
    { lat: 23.450, lng: 85.390 },
    { lat: 23.400, lng: 85.400 },
    { lat: 23.380, lng: 85.350 },
  ],
  ormanjhi: [
    { lat: 23.440, lng: 85.430 },
    { lat: 23.460, lng: 85.450 },
    { lat: 23.430, lng: 85.470 },
    { lat: 23.410, lng: 85.440 },
  ],
};

// Assign different colors for each district
const districtColors = {
  ranchi: { fillColor: 'rgba(0, 100, 200, 0.5)', strokeColor: 'blue' }, // Blue
  dhanbad: { fillColor: 'rgba(0, 200, 100, 0.5)', strokeColor: 'green' }, // Green
  jamshedpur: { fillColor: 'rgba(200, 100, 0, 0.5)', strokeColor: 'orange' }, // Orange
  ormanjhi: { fillColor: 'rgba(200, 200, 0, 0.5)', strokeColor: 'yellow' }, // Yellow
};

// Custom icon paths for P1, P2, P3 (you can replace with actual URLs to your icon images)
const icons = {
  P1: <img src={logo} alt="App Logo" style={{ width: '40px', height: 'auto' }} />, // Replace with actual URL
  P2: <img src={logo} alt="App Logo" style={{ width: '40px', height: 'auto' }} />, // Replace with actual URL
  P3: <img src={logo} alt="App Logo" style={{ width: '40px', height: 'auto' }} />, // Replace with actual URL
};

// Positions for P1, P2, and P3 inside polygons
const polygonIcons = [
  { position: { lat: 23.450, lng: 85.350 }, icon: icons.P1 }, // Inside Ranchi
  { position: { lat: 23.475, lng: 85.390 }, icon: icons.P2 }, // Inside Dhanbad
  { position: { lat: 23.435, lng: 85.375 }, icon: icons.P3 }, // Inside Jamshedpur
];

// Coordinates for Casa Blanca polygon (adjusted)
const casaBlancaCoords = [
  { lat: 23.442592425229485, lng: 85.4608527 },
  { lat: 23.442692, lng: 85.4608527 },
  { lat: 23.443292, lng: 85.4608527 }, // Extend to the east
  { lat: 23.443292, lng: 85.4628527 }, // Extend to the north
  { lat: 23.442692, lng: 85.4628527 },
  { lat: 23.442692, lng: 85.4610527 }, // Extend to the south
  { lat: 23.442592425229485, lng: 85.4610527 },
  { lat: 23.442192, lng: 85.4610527 }, // Extend further south
  { lat: 23.442192, lng: 85.4603527 }, // Extend further west
];

// Coordinates for Casa Blanca marker
const casaBlancaMarker = {
  lat: 23.442592425229485,
  lng: 85.4608527,
};

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your API key
  });

  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [showInfoWindow, setShowInfoWindow] = useState(false);
  const [showLabel, setShowLabel] = useState(false); // State to control label visibility

  const handlePolygonClick = (district) => {
    setSelectedDistrict(district);
    setShowInfoWindow(true);
  };

  // Function to handle map click
  const handleMapClick = () => {
    setShowLabel(true); // Show label on map click
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={6} // Adjust zoom level
      onClick={handleMapClick} // Attach click handler to the map
    >
      {/* Polygons for all districts */}
      {Object.entries(districts).map(([districtName, coords]) => (
        <PolygonF
          key={districtName}
          paths={coords}
          options={{
            fillColor: districtColors[districtName].fillColor, // Assign different fill color
            strokeColor: districtColors[districtName].strokeColor, // Assign different stroke color
            strokeOpacity: 1,
            strokeWeight: 2, // Border thickness
          }}
          onClick={() => handlePolygonClick(districtName)}
        />
      ))}

      {/* Place custom icons labeled P1, P2, P3 inside the polygons */}
      {polygonIcons.map((icon, index) => (
        <MarkerF
          key={index}
          position={icon.position}
          icon={{
            url: icon.icon,
            scaledSize: new window.google.maps.Size(40, 40), // Scale icon size
            labelOrigin: new window.google.maps.Point(20, -10), // Position the label above the icon
          }}
        />
      ))}

      {/* Polygon for Casa Blanca */}
      <PolygonF
        paths={casaBlancaCoords}
        options={{
          fillColor: 'rgba(200, 0, 0, 0.6)', // Dark red fill
          strokeColor: 'darkred', // Dark red border
          strokeOpacity: 1,
          strokeWeight: 2, // Increased border thickness
        }}
      />

      {/* Marker for Casa Blanca with conditional label */}
      <MarkerF
        position={casaBlancaMarker}
        label={showLabel ? { text: 'This is our FPO!', color: 'white', fontSize: '16px', fontWeight: 'bold' } : null}
      />

      {/* Info Window for selected district */}
      {showInfoWindow && selectedDistrict && (
        <InfoWindowF
          position={center} // You can adjust the position as needed
          onCloseClick={() => {
            setShowInfoWindow(false);
            setSelectedDistrict(null);
          }}
        >
          <div>
            <h4>You clicked on {selectedDistrict} district!</h4>
          </div>
        </InfoWindowF>
      )}
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
