import { GoogleMap, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to Ranchi coordinates
const center = {
  lat: 23.3680, // Latitude for Ormanji
  lng: 85.3200, // Longitude for Ormanji
};

// Corrected coordinates for the polygon covering Jharkhand
const jharkhandCoords = [
  { lat: 24.7104, lng: 84.7102 },  // Roughly near UP-Bihar border
  { lat: 24.3900, lng: 87.3355 },  // Near Deoghar, Jharkhand
  { lat: 22.8341, lng: 88.3996 },  // Near West Bengal
  { lat: 22.5991, lng: 86.9780 },  // Near Jamshedpur, Jharkhand
  { lat: 23.3543, lng: 85.3347 },  // Near Ranchi, Jharkhand
  { lat: 23.6936, lng: 84.6554 },  // Closing the polygon
];

// Coordinates for Ranchi
const ranchiCoords = [
    { lat: 23.3697, lng: 85.2203 }, // 23°22'11"N 85°13'13"E
    { lat: 23.3181, lng: 85.2467 }, // 23°19'05"N 85°14'48"E
    { lat: 23.2417, lng: 85.3036 }, // 23°14'30"N 85°18'13"E
    { lat: 23.3111, lng: 85.3928 }, // 23°18'40"N 85°23'34"E
    { lat: 23.3886, lng: 85.4356 }, // 23°23'19"N 85°26'08"E
    { lat: 23.4058, lng: 85.4153 }, // 23°24'21"N 85°24'55"E
    { lat: 23.4394, lng: 85.3589 }, // 23°26'22"N 85°21'32"E
    { lat: 23.4481, lng: 85.3019 }, // 23°26'53"N 85°18'07"E
    { lat: 23.4058, lng: 85.2614 }, // 23°24'21"N 85°15'41"E
    { lat: 23.4281, lng: 85.2169 }, // 23°25'41"N 85°13'01"Elose the polygon
];

// Coordinates for Casa Blanca Estate in Rukka, Jharkhand
const casaBlancaCoords = { lat: 23.443576748453125, lng: 85.46080978467886 };

// Coordinates for Ormanji
const ormanjiCoords = { lat: 23.3680, lng: 85.3200 };

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your Google Maps API key
  });

  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);
  const [showRanchiPolygon, setShowRanchiPolygon] = useState(false);

  const handleJharkhandPolygonClick = () => {
    setShowJharkhandPolygon(false);  // Hide Jharkhand polygon
    setShowRanchiPolygon(true);       // Show Ranchi polygon
  };

  return isLoaded ? (
    <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={10}>
      {/* Polygon covering Jharkhand */}
      {showJharkhandPolygon && (
        <PolygonF
          paths={jharkhandCoords}
          options={{
            fillColor: '#FF0000', // Fill color for Jharkhand polygon
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={handleJharkhandPolygonClick} // Click event to hide Jharkhand polygon and show Ranchi
        />
      )}

      {/* Polygon covering Ranchi */}
      {showRanchiPolygon && (
        <PolygonF
          paths={ranchiCoords}
          options={{
            fillColor: '#0000FF', // Fill color for Ranchi polygon
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
        />
      )}

      {/* Marker on Casa Blanca Estate */}
      <MarkerF
        position={casaBlancaCoords}
        title="Casa Blanca Estate, Rukka"
      />

      {/* Marker for Ormanji */}
      <MarkerF
        position={ormanjiCoords}
        title="Ormanji"
      />
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
