import axios from 'axios'; // If using axios for HTTP requests
import React, { useEffect, useState } from 'react';
import {
  Bar,
  BarChart,
  CartesianGrid,
  Legend,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from 'recharts';
import './Dashboard.css';

const Dashboard = () => {
  // State to hold data and loading status
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrman, setSelectedOrman] = useState('Orma_00001'); // Default selected Orman

  // Fetch data from backend API based on selected Orman
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Fetch data based on selected Orman
        const response = await axios.get(`http://localhost:8080/growSphere/yeild?orman=${selectedOrman}`); 
        setData(response.data); // Assuming the API returns an array of objects with {name, quantity}
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedOrman]);

  // Handle Orman selection change
  const handleOrmanChange = (event) => {
    setSelectedOrman(event.target.value);
  };

  return (
    <div className="dashboard-container">
      {/* Dropdown search bar for Orman selection */}
      <div className="search-container">
        <label>Select Orman:</label>
        <select 
          value={selectedOrman} 
          onChange={handleOrmanChange} 
          className="search-dropdown"
        >
          <option value="Orma_00001">Orma_00001</option>
          <option value="Orma_00002">Orma_00002</option>
          <option value="Orma_00003">Orma_00003</option>
        </select>
        <button className="search-button">Search</button>
      </div>

      {/* Chart section */}
      <div className="chart-container">
        <h3>Total Quantity (MT)</h3>
        {loading ? (
          <p>Loading data...</p> // Loading indicator
        ) : (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="quantity" fill="#8B4513" barSize={40} />
            </BarChart>
          </ResponsiveContainer>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
