import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useCallback, useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to India level
const center = {
  lat: 20.5937,  // Latitude of India
  lng: 78.9629,  // Longitude of India
};

const zoomLevel = 5;  // Default zoom level

// Coordinates for the Jharkhand polygon
const jharkhandCoords = [
  { lat: 24.3011, lng: 84.4983 },
  { lat: 24.4260, lng: 84.3156 },
  { lat: 24.5617, lng: 84.2892 },
  { lat: 24.5683, lng: 84.1817 },
  { lat: 24.5303, lng: 84.0994 },
  { lat: 24.6567, lng: 83.9983 },
  { lat: 24.5661, lng: 83.9333 },
  { lat: 24.5497, lng: 83.8011 },
  { lat: 24.5467, lng: 83.5272 },
  { lat: 24.5225, lng: 83.3947 },
  { lat: 24.4186, lng: 85.0219 },
  { lat: 24.4003, lng: 84.8878 },
  { lat: 24.4262, lng: 85.0422 },
];

// Coordinates for the Ranchi polygon
const ranchiCoords = [
  { lat: 23.3569, lng: 85.3340 },
  { lat: 23.3722, lng: 85.3332 },
  { lat: 23.3720, lng: 85.3143 },
  { lat: 23.3600, lng: 85.3140 },
  { lat: 23.3575, lng: 85.3267 },
];

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your Google Maps API key
  });

  const [map, setMap] = useState(null);
  const [showRanchi, setShowRanchi] = useState(false); // State to track Ranchi visibility

  const handleZoomIn = useCallback(() => {
    if (!map) return;

    const bounds = new window.google.maps.LatLngBounds();
    jharkhandCoords.forEach(coord => bounds.extend(coord));
    map.fitBounds(bounds);
    setShowRanchi(true); // Show Ranchi polygon and button after clicking Jharkhand
  }, [map]);

  const onLoad = useCallback((mapInstance) => {
    setMap(mapInstance);
  }, []);

  const handleRanchiClick = useCallback(() => {
    const bounds = new window.google.maps.LatLngBounds();
    ranchiCoords.forEach(coord => bounds.extend(coord));
    map.fitBounds(bounds);
  }, [map]);

  return isLoaded ? (
    <div style={{ position: 'relative' }}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={zoomLevel}
        onLoad={onLoad}
      >
        {/* Jharkhand Polygon */}
        <PolygonF
          paths={jharkhandCoords}
          options={{
            fillColor: '#00FF00',
            strokeColor: '#000000',
            fillOpacity: 0.5,
          }}
          onClick={handleZoomIn}  // Zoom into Jharkhand when clicked
        />

        {/* Ranchi Polygon and Button */}
        {showRanchi && (
          <>
            <PolygonF
              paths={ranchiCoords}
              options={{
                fillColor: '#FF5733', // Different color for Ranchi
                strokeColor: '#000000',
                fillOpacity: 0.5,
              }}
              onClick={handleRanchiClick} // Zoom into Ranchi when clicked
            />
            <button
              onClick={handleRanchiClick} // Zoom into Ranchi when clicked
              style={{
                position: 'absolute',
                top: '30%',  // Adjust the position as needed
                left: '50%',
                transform: 'translate(-50%, -50%)', // Center the button
                padding: '10px 20px',
                backgroundColor: 'transparent',
                border: '2px solid #FF5733',
                color: '#FF5733',
                fontSize: '16px',
                cursor: 'pointer',
                borderRadius: '5px',
              }}
            >
              Press Here
            </button>
          </>
        )}
      </GoogleMap>
    </div>
  ) : <></>;
};

export default MapComponent;
