import axios from 'axios'; // If using axios for HTTP requests
import React, { useEffect, useState } from 'react';
import {
  Bar,
  BarChart,
  CartesianGrid,
  Legend,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from 'recharts';
import './Dashboard.css';

const Dashboard = () => {
  // State to hold data and loading status
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch data from backend API
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Replace with your Spring Boot API URL
        const response = await axios.get('http://localhost:8080/growSphere/yeild'); 
        setData(response.data); // Assuming the API returns an array of objects with {name, quantity}
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="dashboard-container">
      {/* Search bar section */}
      <div className="search-container">
        <input 
          type="text" 
          placeholder="Orma_00001" 
          className="search-input" 
        />
        <button className="search-button">Search</button>
      </div>

      {/* Filters section */}
      <div className="filters-container">
        <input type="text" placeholder="State id" />
        <input type="text" placeholder="District id" />
        <input type="text" placeholder="FPO id" />
        <input type="text" placeholder="Crop id" />
        <input type="text" placeholder="Farmer id" />
      </div>

      {/* Chart section */}
      <div className="chart-container">
        <h3>Total Quantity (MT)</h3>
        {loading ? (
          <p>Loading data...</p> // Loading indicator
        ) : (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="quantity" fill="#8B4513" barSize={40} />
            </BarChart>
          </ResponsiveContainer>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
