import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to India level
const center = {
  lat: 20.5937,  // Latitude of India
  lng: 78.9629,  // Longitude of India
};

const zoomLevel = 5;  // Adjusted zoom level for the India view

// Coordinates for the Jharkhand polygon
const jharkhandCoords = [
  { lat: 24.3011, lng: 84.4983 },
  { lat: 24.4260, lng: 84.3156 },
  { lat: 24.5617, lng: 84.2892 },
  { lat: 24.5683, lng: 84.1817 },
  { lat: 24.5303, lng: 84.0994 },
  { lat: 24.6567, lng: 83.9983 },
  { lat: 24.5661, lng: 83.9333 },
  { lat: 24.5497, lng: 83.8011 },
  { lat: 24.5467, lng: 83.5272 },
  { lat: 24.5225, lng: 83.3947 },
  { lat: 24.4186, lng: 85.0219 },
  { lat: 24.4003, lng: 84.8878 },
  { lat: 24.4262, lng: 85.0422 },
];

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk",
  });

  const [map, setMap] = useState(null);
  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);

  const handlePolygonClick = (polygonCoords, zoomLevel) => {
    if (!map) return; // Ensure map is defined
    const bounds = new window.google.maps.LatLngBounds();
    polygonCoords.forEach(coord => bounds.extend(coord));
    const polygonCenter = bounds.getCenter();

    map.setCenter(polygonCenter);
    map.setZoom(zoomLevel);
  };

  // Set the map instance when the GoogleMap is ready
  const onLoad = (mapInstance) => {
    setMap(mapInstance);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={zoomLevel}
      onLoad={onLoad} // Store the map instance on load
    >
      {showJharkhandPolygon && (
        <PolygonF
          paths={jharkhandCoords}
          options={{ fillColor: '#00FF00', strokeColor: '#000000' }}
          onClick={() => handlePolygonClick(jharkhandCoords, 8)} // Zoom to level 8 on click
        />
      )}
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
