  { lat: 25.1114, lng: 87.2681 },
  { lat: 24.8217, lng: 85.7231 },
  { lat: 24.5261, lng: 83.4228 }, // Closing the polygon
];

// Coordinates for Ranchi
const ranchiCoords = [
  { lat: 23.3697, lng: 85.2203 },
  { lat: 23.3181, lng: 85.2467 },
  { lat: 23.2417, lng: 85.3036 },
  { lat: 23.3111, lng: 85.3928 },
  { lat: 23.3886, lng: 85.4356 },
  { lat: 23.4058, lng: 85.4153 },
  { lat: 23.4394, lng: 85.3589 },
  { lat: 23.4481, lng: 85.3019 },
  { lat: 23.4058, lng: 85.2614 },
  { lat: 23.4281, lng: 85.2169 },
];

// Coordinates for the new polygon
const newPolygonCoords = [
  { lat: 23.4908, lng: 85.4683 },
  { lat: 23.4836, lng: 85.4647 },
  { lat: 23.4794, lng: 85.4678 },
  { lat: 23.4769, lng: 85.4725 },
  { lat: 23.4758, lng: 85.4803 },
  { lat: 23.4800, lng: 85.4850 },
  { lat: 23.4819, lng: 85.4872 },
  { lat: 23.4911, lng: 85.4889 },
  { lat: 23.4900, lng: 85.4753 },
  { lat: 23.4917, lng: 85.4686 }, // Closing the polygon
];

// Coordinates for the red polygon
const redPolygonCoords = [
    { lat: 23.4525, lng: 85.4569 },
    { lat: 23.4453, lng: 85.4525 },
    { lat: 23.4382, lng: 85.4620 },
    { lat: 23.4284, lng: 85.4708 },
    { lat: 23.4289, lng: 85.4739 },
    { lat: 23.4372, lng: 85.4738 },
    { lat: 23.4460, lng: 85.4800 },
    { lat: 23.4500, lng: 85.4713 },
    { lat: 23.4515, lng: 85.4703 },
    { lat: 23.4512, lng: 85.4621 } // Removed the trailing comma
  ]; // Closing the array properly

  // Coordinates for the yellow polygon
  const yellowPolygonCoords = [
    { lat: 23.4458, lng: 85.4561 },
    { lat: 23.4393, lng: 85.4569 },
    { lat: 23.4389, lng: 85.4617 },
    { lat: 23.4443, lng: 85.4636 },
    { lat: 23.4484, lng: 85.4622 },
    { lat: 23.4455, lng: 85.4560 },
    { lat: 23.4458, lng: 85.4561 } // Closing the polygon
  ]; // Closing the array properly
   // Removed the trailing comma
   const newPolygonNearbyCoords = [
    { lat: 23.4500, lng: 85.4600 },  // New point 1
    { lat: 23.4525, lng: 85.4630 },  // New point 2
    { lat: 23.4510, lng: 85.4680 },  // New point 3
    { lat: 23.4480, lng: 85.4670 },  // New point 4
    { lat: 23.4465, lng: 85.4625 },  // New point 5
    { lat: 23.4480, lng: 85.4605 },  // New point 6
    { lat: 23.4500, lng: 85.4600 },  // Closing the polygon
  ];
  const thirdPolygonCoords = [
    { lat: 23.4525, lng: 85.4680 },  // New point 1 (touching the second polygon)
    { lat: 23.4545, lng: 85.4720 },  // New point 2
    { lat: 23.4515, lng: 85.4760 },  // New point 3
    { lat: 23.4485, lng: 85.4745 },  // New point 4
    { lat: 23.4470, lng: 85.4705 },  // New point 5 (near the edge of second polygon)
    { lat: 23.4525, lng: 85.4680 },  // Closing the polygon (touching)
  ];
  const additionalPolygon1Coords = [
    { lat: 23.4458, lng: 85.4622 }, // Start from the right corner of the yellow polygon
    { lat: 23.4393, lng: 85.4630 }, // Slightly up
    { lat: 23.4389, lng: 85.4660 }, // Further up
    { lat: 23.4443, lng: 85.4660 }, // Maintain the height
    { lat: 23.4484, lng: 85.4645 }, // Down a bit
    { lat: 23.4455, lng: 85.4625 }, // Close to the original point
    { lat: 23.4458, lng: 85.4622 }// Closing the polygon
];




// Coordinates for Casa Blanca Estate in Rukka, Jharkhand
const casaBlancaCoords = { lat: 23.443576748453125, lng: 85.46080978467886 };

// Coordinates for Ormanji
const ormanjiCoords = { lat: 23.3680, lng: 85.3200 };

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk",
  });

  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);
  const [showRanchiPolygon, setShowRanchiPolygon] = useState(true);
  const [showNewPolygon, setShowNewPolygon] = useState(true);
  const [showRedPolygon, setShowRedPolygon] = useState(true);
  const [showYellowPolygon, setShowYellowPolygon] = useState(true); // Changed to state
  const [shownewPolygonNearbyCoords , setShownewPolygonNearbyCoords] = useState(true);
  const [showthirdPolygonCoords , setShowthirdPolygonCoords] = useState(true);
  const [showadditionalPolygon1Coords , setShowadditionalPolygon1Coords] = useState(true);


  // Handler to hide polygons
  const handlePolygonClick = (polygon) => {
    switch (polygon) {
      case 'Jharkhand':
        setShowJharkhandPolygon(false);
        break;
      case 'Ranchi':
        setShowRanchiPolygon(false);
        break;
      case 'New':
        setShowNewPolygon(false);
        break;
      case 'Red':
        setShowRedPolygon(false);
        break;
      default:
        break;
    }
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={zoomLevel}
    >
      {/* Polygon covering Jharkhand */}
      {showJharkhandPolygon && (
        <PolygonF
          key="jharkhand"
          paths={jharkhandCoords}
          options={{
            fillColor: '#228B22',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={() => handlePolygonClick('Jharkhand')}
        />
      )}

      {/* Polygon covering Ranchi */}
      {showRanchiPolygon && (
        <PolygonF
          key="ranchi"
          paths={ranchiCoords}
          options={{
            fillColor: '#0000FF',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={() => handlePolygonClick('Ranchi')}
        />
      )}

      {/* New polygon */}
      {showNewPolygon && (
        <PolygonF
          key="newPolygon"
          paths={newPolygonCoords}
          options={{
            fillColor: '#00FF00',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={() => handlePolygonClick('New')}
        />
      )}

      {/* Red polygon */}
      {showRedPolygon && (
        <PolygonF
          key="redPolygon"
          paths={redPolygonCoords}
          options={{
            fillColor: '#FF0000',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={() => handlePolygonClick('Red')}
        />
      )}

      {/* Yellow polygon */}
      {showYellowPolygon && (
        <PolygonF
          key="yellowPolygon"
          paths={yellowPolygonCoords}
          options={{
            fillColor: '#FFFF00',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
        />
      )}
       {shownewPolygonNearbyCoords && (
        <PolygonF
          key="newPolygonNearbyCoords"
          paths={newPolygonNearbyCoords}
          options={{
            fillColor: '#228B22',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}

        />
      )}
       {showthirdPolygonCoords && (
        <PolygonF
          key="newPolygonNearbyCoords"
          paths={thirdPolygonCoords}
          options={{
            fillColor: '#228B22',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}

        />
      )}
       {showadditionalPolygon1Coords && (
        <PolygonF
          key="newPolygonNearbyCoords"
          paths={additionalPolygon1Coords}
          options={{
            fillColor: '#228B22',
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}

        />
      )}

      {/* Marker on Casa Blanca Estate */}
      <MarkerF
        position={casaBlancaCoords}
        title="Casa Blanca Estate, Rukka"
      />

      {/* Marker for Ormanji */}
      <MarkerF
        position={ormanjiCoords}
        title="Ormanji"
      />
    </GoogleMap>
  ) : null; // Return null while loading
};
// At the bottom of MapComponent.js file
export default MapComponent;
