import { GoogleMap, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useCallback, useRef } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to Jharkhand level
const center = {
  lat: 23.6102,  // Latitude of Jharkhand
  lng: 85.2799,  // Longitude of Jharkhand
};

const zoomLevel = 6;  // Initial zoom level for a broader view

// Coordinates for the Jharkhand polygon
const jharkhandCoords = [
  { lat: 24.4366, lng: 84.2201 },  // Northern border
  { lat: 24.1851, lng: 85.0568 },  // Slightly south
  { lat: 23.9867, lng: 85.6182 },  // Mid-north
  { lat: 23.6255, lng: 86.4194 },  // North-east
  { lat: 23.1986, lng: 86.8941 },  // Eastern side
  { lat: 22.7767, lng: 86.6826 },  // South-east
  { lat: 22.3571, lng: 85.9870 },  // Southern border
  { lat: 22.2786, lng: 85.3999 },  // South-west
  { lat: 22.7027, lng: 84.8895 },  // West
  { lat: 23.1097, lng: 84.6137 },  // Mid-west
  { lat: 23.7197, lng: 84.4003 },  // North-west
  { lat: 24.0020, lng: 84.4666 },  // Closing the boundary
];

// Coordinates for the Ranchi polygon
const ranchiCoords = [
  { lat: 23.4754529297811, lng: 85.47999773329536 },
  { lat: 23.476233545770825, lng: 85.48133511299329 },
  { lat: 23.482172002424274, lng: 85.48793069910942 },
  { lat: 23.484629723351897, lng: 85.49177543218504 },
  { lat: 23.487755237059364, lng: 85.49270748868825 },
  { lat: 23.491308081463988, lng: 85.48932878386417 },
  { lat: 23.490880676677126, lng: 85.48664912141749 },
  { lat: 23.491040953634585, lng: 85.48210534596441 },
  { lat: 23.491788910192813, lng: 85.48041599355238 },
  { lat: 23.490666973764053, lng: 85.47756157051137 },
  { lat: 23.490239566898666, lng: 85.47517317572192 },
  { lat: 23.49173548486511, lng: 85.46888179432537 },
  { lat: 23.491844895394937, lng: 85.46869079584923 },
  { lat: 23.483704922251732, lng: 85.46480023672797 },
  { lat: 23.47969050391436, lng: 85.46771815606891 },
  { lat: 23.479021422311977, lng: 85.46929869571193 },
];

// Coordinates for Casa Blanca in Ormanjhi
const casaBlancaCoords = { lat: 23.442592425229485, lng: 85.4608527 };

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your Google Maps API key
  });

  const mapRef = useRef(null);  // Using useRef instead of state to store map instance

  // Save the map instance when loaded
  const onLoad = useCallback((mapInstance) => {
    console.log("Map loaded:", mapInstance);
    mapRef.current = mapInstance;  // Store map instance in ref
  }, []);

  // Zoom in and fit the polygon's bounds when clicked
  const handlePolygonClick = useCallback((polygonCoords) => {
    const map = mapRef.current;
    if (!map) {
      console.log("Map instance not available.");
      return;
    }

    console.log("Polygon clicked, zooming in...");

    const bounds = new window.google.maps.LatLngBounds();
    polygonCoords.forEach(coord => bounds.extend(coord));
    map.fitBounds(bounds);  // Fit the bounds of the polygon

    // Set zoom level to stay within bounds
    const listener = window.google.maps.event.addListener(map, "idle", () => {
      if (map.getZoom() > 10) {
        map.setZoom(10);  // Ensure zoom level does not go above 10
      }
      window.google.maps.event.removeListener(listener); // Remove the listener after zooming
    });
  }, []);

  return isLoaded ? (
    <div style={{ position: 'relative' }}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={zoomLevel}
        onLoad={onLoad}
      >
        {/* Jharkhand Polygon */}
        <PolygonF
          paths={jharkhandCoords}
          options={{
            fillColor: '#00FF00',
            strokeColor: '#000000',
            fillOpacity: 0.5,
          }}
          onClick={() => {
            console.log("Jharkhand polygon clicked");
            handlePolygonClick(jharkhandCoords);  // Zoom into Jharkhand when clicked
          }}
        />

        {/* Ranchi Polygon */}
        <PolygonF
          paths={ranchiCoords}
          options={{
            fillColor: '#FF5733',
            strokeColor: '#000000',
            fillOpacity: 0.5,
          }}
          onClick={() => {
            console.log("Ranchi polygon clicked");
            handlePolygonClick(ranchiCoords);  // Zoom into Ranchi when clicked
          }}
        />

        {/* Casa Blanca Marker */}
        <MarkerF
          position={casaBlancaCoords}
          title="Casa Blanca Ormanjhi"
        />
      </GoogleMap>
    </div>
  ) : <></>;
};

export default MapComponent;
