import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to Ormanji coordinates
const center = {
  lat: 23.3680, // Latitude for Ormanji
  lng: 85.3200, // Longitude for Ormanji
};

// Corrected coordinates for the polygon covering Jharkhand
const jharkhandCoords = [
  { lat: 24.7104, lng: 84.7102 },  // Roughly near UP-Bihar border
  { lat: 24.3900, lng: 87.3355 },  // Near Deoghar, Jharkhand
  { lat: 22.8341, lng: 88.3996 },  // Near West Bengal
  { lat: 22.5991, lng: 86.9780 },  // Near Jamshedpur, Jharkhand
  { lat: 23.3543, lng: 85.3347 },  // Near Ranchi, Jharkhand
  { lat: 23.6936, lng: 84.6554 },  // Closing the polygon
];

// Coordinates for Ormanji
const ormanjiCoords = [
  { lat: 23.3680, lng: 85.3200 },
  { lat: 23.3720, lng: 85.3200 },
  { lat: 23.3700, lng: 85.3250 },
  { lat: 23.3680, lng: 85.3250 },
  { lat: 23.3680, lng: 85.3200 }, // Close the polygon
];

// Coordinates for a larger Ormanji covering polygon
const ormanjiCoverCoords = [
  { lat: 23.3670, lng: 85.3180 },
  { lat: 23.3730, lng: 85.3180 },
  { lat: 23.3730, lng: 85.3270 },
  { lat: 23.3670, lng: 85.3270 },
  { lat: 23.3670, lng: 85.3180 }, // Close the polygon
];

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your Google Maps API key
  });

  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);
  const [showOrmanjiCoverPolygon, setShowOrmanjiCoverPolygon] = useState(false);

  const handleJharkhandPolygonClick = () => {
    setShowJharkhandPolygon(false);
    setShowOrmanjiCoverPolygon(true); // Show the new Ormanji covering polygon
  };

  return isLoaded ? (
    <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={7}>
      {/* Polygon covering Jharkhand */}
      {showJharkhandPolygon && (
        <PolygonF
          paths={jharkhandCoords}
          options={{
            fillColor: '#FF0000', // Fill color for Jharkhand polygon
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.6,
          }}
          onClick={handleJharkhandPolygonClick} // Click event to hide the Jharkhand polygon and show Ormanji cover
        />
      )}

      {/* Polygon covering Ormanji */}
      <PolygonF
        paths={ormanjiCoords}
        options={{
          fillColor: '#00FF00', // Fill color for Ormanji polygon
          strokeColor: '#000000',
          strokeWeight: 2,
          fillOpacity: 0.6,
        }}
      />

      {/* New Polygon that covers Ormanji area completely */}
      {showOrmanjiCoverPolygon && (
        <PolygonF
          paths={ormanjiCoverCoords}
          options={{
            fillColor: '#0000FF', // Fill color for larger Ormanji cover polygon
            strokeColor: '#000000',
            strokeWeight: 2,
            fillOpacity: 0.4,
          }}
        />
      )}
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
