import axios from 'axios'; // If using axios for HTTP requests
import React, { useEffect, useState } from 'react';
import {
  Bar,
  BarChart,
  CartesianGrid,
  Legend,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from 'recharts';
import './Dashboard.css';

const Dashboard = () => {
  // State to hold data, search term, and loading status
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]); // State to hold filtered data
  const [searchTerm, setSearchTerm] = useState(''); // State for search input
  const [loading, setLoading] = useState(true);

  // Fetch data from backend API
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Replace with your Spring Boot API URL
        const response = await axios.get('http://localhost:8080/growSphere/yeild');
        setData(response.data); // Assuming the API returns an array of objects with {name, quantity}
        setFilteredData(response.data); // Initially set filteredData to all data
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Handle search button click
  const handleSearch = () => {
    if (searchTerm.trim() === '') {
      setFilteredData(data); // If search is empty, reset to all data
    } else {
      // Filter data based on the search term (assuming "name" field is being searched)
      const filtered = data.filter(item => item.name.toLowerCase().includes(searchTerm.toLowerCase()));
      setFilteredData(filtered);
    }
  };

  return (
    <div className="dashboard-container">
      {/* Search bar section */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter name (e.g., Orma_00001)"
          className="search-input"
          value={searchTerm} // Bind the search term to the input
          onChange={(e) => setSearchTerm(e.target.value)} // Update state when user types
        />
        <button className="search-button" onClick={handleSearch}>Search</button>
      </div>

      {/* Filters section */}
      <div className="filters-container">
        <input type="text" placeholder="State id" />
        <input type="text" placeholder="District id" />
        <input type="text" placeholder="FPO id" />
        <input type="text" placeholder="Crop id" />
        <input type="text" placeholder="Farmer id" />
      </div>

      {/* Chart section */}
      <div className="chart-container">
        <h3>Total Quantity (MT)</h3>
        {loading ? (
          <p>Loading data...</p> // Loading indicator
        ) : (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={filteredData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="quantity" fill="#8B4513" barSize={40} />
            </BarChart>
          </ResponsiveContainer>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
