import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to India level
const center = {
  lat: 20.5937,  // Latitude of India
  lng: 78.9629,  // Longitude of India
};

const zoomLevel = 5;  // Adjusted zoom level for the India view

// Coordinates for the Jharkhand polygon
const jharkhandCoords = [
  { lat: 24.4125, lng: 83.4258 },
  { lat: 22.5828, lng: 84.0825 },
  { lat: 22.1931, lng: 85.0492 },
  { lat: 22.3264, lng: 86.4536 },
  { lat: 22.6908, lng: 86.5150 },
  { lat: 23.3169, lng: 85.8747 },
  { lat: 23.6617, lng: 86.6256 },
  { lat: 24.5783, lng: 87.8997 },
  { lat: 25.1028, lng: 87.7900 },
  { lat: 25.1114, lng: 87.2681 },
  { lat: 24.8217, lng: 85.7231 },
  { lat: 24.5261, lng: 83.4228 }, // Closing the polygon
];

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk",
  });
  const [map, setMap] = useState(null);

  const handlePolygonClick = (polygonCoords, zoomLevel) => {
    // Calculate the center of the polygon (can also use a predefined center)
    const bounds = new window.google.maps.LatLngBounds();
    polygonCoords.forEach(coord => bounds.extend(coord));
    const polygonCenter = bounds.getCenter();

    // Set the map's center and zoom level
    map.setCenter(polygonCenter);
    map.setZoom(zoomLevel);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={zoomLevel}
      onLoad={map => setMap(map)}
    >
      {/* Jharkhand polygon with zoom-in functionality on click */}
      <PolygonF 
        paths={jharkhandCoords} 
        options={{ fillColor: '#00FF00', strokeColor: '#000000' }}
        onClick={() => handlePolygonClick(jharkhandCoords, 8)}  // Zoom in on click
      />
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
