import { GoogleMap, InfoWindowF, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center the map on Jharkhand
const center = {
  lat: 23.3441,
  lng: 85.3096,
};

// Coordinates for major districts in Jharkhand including Ormanjhi
const districts = {
  // ... other districts
  ormanjhi: [
    { lat: 23.438, lng: 85.367 },
    { lat: 23.438, lng: 85.397 },
    { lat: 23.418, lng: 85.397 },
    { lat: 23.418, lng: 85.367 },
  ],
};

// Coordinates for Casa Blanca polygon
const casaBlancaCoords = [
  { lat: 23.442592425229485, lng: 85.4608527 },
  { lat: 23.442692, lng: 85.4608527 },
  { lat: 23.442692, lng: 85.4618527 },
  { lat: 23.442592425229485, lng: 85.4618527 },
];

// Coordinates for Casa Blanca marker
const casaBlancaMarker = {
  lat: 23.442592425229485,
  lng: 85.4608527,
};

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // Replace with your API key
  });

  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [showInfoWindow, setShowInfoWindow] = useState(false);

  const handlePolygonClick = (district) => {
    setSelectedDistrict(district);
    setShowInfoWindow(true);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={6} // Adjust zoom level
    >
      {/* Polygons for all districts */}
      {Object.entries(districts).map(([districtName, coords]) => (
        <PolygonF
          key={districtName}
          paths={coords}
          options={{
            fillColor: 'rgba(0, 50, 100, 0.6)', // Dark blue fill
            strokeColor: 'darkblue', // Dark blue border
            strokeOpacity: 1,
            strokeWeight: 2, // Increased border thickness
          }}
          onClick={() => handlePolygonClick(districtName)}
        />
      ))}

      {/* Polygon for Casa Blanca */}
      <PolygonF
        paths={casaBlancaCoords}
        options={{
          fillColor: 'rgba(200, 0, 0, 0.6)', // Dark red fill
          strokeColor: 'darkred', // Dark red border
          strokeOpacity: 1,
          strokeWeight: 2, // Increased border thickness
        }}
      />

      {/* Marker for Casa Blanca */}
      <MarkerF
        position={casaBlancaMarker}
        onClick={() => setShowInfoWindow(true)}
      />

      {/* Info Window for selected district */}
      {selectedDistrict && showInfoWindow && (
        <InfoWindowF
          position={casaBlancaMarker} // Position it at Casa Blanca marker
          onCloseClick={() => {
            setShowInfoWindow(false);
            setSelectedDistrict(null);
          }}
        >
          <div>
            <h4>You clicked on {selectedDistrict} district!</h4>
          </div>
        </InfoWindowF>
      )}
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
