import React, { useState } from 'react';
import { GoogleMap, InfoWindow, LoadScript, Marker } from "@react-google-maps/api";
import { Auth0Provider, useAuth0 } from '@auth0/auth0-react';
import { Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import About from './components/About';
import Contact from './components/Contact';
import Header from './components/Header';
import Home from './components/Home';
import MyMapComponent from './components/MyMapComponent';
import Profile from './components/Profile';

function App() {
  const { isAuthenticated, loginWithRedirect, logout } = useAuth0(); // Access Auth0 hooks
  const [showMap, setShowMap] = useState(false); // State to manage map visibility

  const mapContainerStyle = {
    height: "400px",
    width: "800px"
  };

  const center = {
    lat: -34.397,
    lng: 150.644
  };

  const handleMapClick = () => {
    setShowMap(true); // Show the map when the button is clicked
  };

  const handleHideMapClick = () => {
    setShowMap(false); // Hide the map when the button is clicked
  };

  return (
    <div>
      <Router>
        <Header />
        <Routes>
          <Route path="/home" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/map" element={<MyMapComponent />} />
          <Route path="/" element={<Home />} />
        </Routes>
      </Router>

      {!isAuthenticated ? ( // Show login button if not authenticated
        <button onClick={loginWithRedirect}>Login</button>
      ) : (
        <>
          <button onClick={handleMapClick}>Show Map</button> {/* Button to show the map */}
          <button onClick={handleHideMapClick}>Hide Map</button> {/* Button to hide the map */}
          <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button> {/* Logout button */}
        </>
      )}

      {showMap && ( // Conditional rendering of the map
        <LoadScript googleMapsApiKey="AIzaSyC1h_ObXgGBpji0Z2koTTQp5qhPbvmUckc">
          <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={center}
            zoom={14}
          >
            <Marker position={center} />
            <InfoWindow position={center}>
              <div>Info Window Content</div>
            </InfoWindow>
          </GoogleMap>
        </LoadScript>
      )}
    </div>
  );
}

// Wrap App component with Auth0Provider
const Root = () => (
  <Auth0Provider
    domain="dev-0va1gvwgwfk0n27p.us.auth0.com"
    clientId="G4oOtJbNvzl9dn8c4bSdoa5EdZsxoOIk"
    authorizationParams={{
      redirect_uri: window.location.origin
    }}
  >
    <App />
  </Auth0Provider>
);

export default Root;
