import { GoogleMap, InfoWindowF, MarkerF, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React from 'react';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Coordinates for Ranchi, Jharkhand, India
const center = {
  lat: 23.3441,
  lng: 85.3096
};

// Another location nearby (can be adjusted)
const center2 = {
  lat: 23.3441,
  lng: 85.3099
};

// Define multiple polygons with sets of coordinates
const polygons = [
  {
    id: 1,
    paths: [
      { lat: 23.3917, lng: 85.2799 },
      { lat: 23.3917, lng: 85.3350 },
      { lat: 23.3401, lng: 85.3350 },
      { lat: 23.3401, lng: 85.2799 }
    ],
    fillColor: 'rgba(0, 255, 0, 0.2)', // Light green
    strokeColor: 'green'
  },
  {
    id: 2,
    paths: [
      { lat: 23.3680, lng: 85.2900 },
      { lat: 23.3680, lng: 85.3100 },
      { lat: 23.3250, lng: 85.3100 },
      { lat: 23.3250, lng: 85.2900 }
    ],
    fillColor: 'rgba(255, 0, 0, 0.2)', // Light red
    strokeColor: 'red'
  },
  {
    id: 3,
    paths: [
      { lat: 23.3600, lng: 85.3200 },
      { lat: 23.3600, lng: 85.3350 },
      { lat: 23.3450, lng: 85.3350 },
      { lat: 23.3450, lng: 85.3200 }
    ],
    fillColor: 'rgba(0, 0, 255, 0.2)', // Light blue
    strokeColor: 'blue'
  }
];

function MapComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk"
  });

  const [map, setMap] = React.useState(null);
  const [selectedMarker, setSelectedMarker] = React.useState(null); // State to manage the selected marker

  const onLoad = React.useCallback((map) => {
    map.setZoom(12);
    setMap(map);
  }, []);

  const onUnmount = React.useCallback((map) => {
    setMap(null);
  }, []);

  const handleMarkerClick = (marker) => {
    setSelectedMarker(marker);
  };

  const handleInfoWindowClose = () => {
    setSelectedMarker(null);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={12}
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      {/* Marker at the first location */}
      <MarkerF
        key={1}
        position={center}
        onClick={() => handleMarkerClick('Location 1')}
      />
      {/* Marker at the second location */}
      <MarkerF
        key={2}
        position={center2}
        onClick={() => handleMarkerClick('Location 2')}
      />

      {/* Render multiple polygons */}
      {polygons.map(polygon => (
        <PolygonF
          key={polygon.id}
          paths={polygon.paths}
          options={{
            fillColor: polygon.fillColor,
            strokeColor: polygon.strokeColor,
            strokeOpacity: 1,
            strokeWeight: 2,
          }}
        />
      ))}

      {/* InfoWindow for the first marker */}
      {selectedMarker === 'Location 1' && (
        <InfoWindowF
          position={center}
          onCloseClick={handleInfoWindowClose}
        >
          <div>
            <h4>This is Location 1</h4>
            <p>Description or additional information about Location 1.</p>
          </div>
        </InfoWindowF>
      )}

      {/* InfoWindow for the second marker */}
      {selectedMarker === 'Location 2' && (
        <InfoWindowF
          position={center2}
          onCloseClick={handleInfoWindowClose}
        >
          <div>
            <h4>This is Location 2</h4>
            <p>Description or additional information about Location 2.</p>
          </div>
        </InfoWindowF>
      )}

      { /* You can add more child components, such as more markers, info windows, etc. */ }
      <></>
    </GoogleMap>
  ) : <></>;
}

export default MapComponent;
