import { GoogleMap, PolygonF, useJsApiLoader } from '@react-google-maps/api';
import React, { useState } from 'react';

const containerStyle = {
  width: '100%',
  height: '600px',
};

// Center point set to India level
const center = {
  lat: 20.5937,  // Latitude of India
  lng: 78.9629,  // Longitude of India
};

const zoomLevel = 5;  // Adjusted zoom level for the India view

// Coordinates for the Jharkhand polygon
const jharkhandCoords = [
  { lat: 24.4125, lng: 83.4258 },
  { lat: 22.5828, lng: 84.0825 },
  { lat: 22.1931, lng: 85.0492 },
  { lat: 22.3264, lng: 86.4536 },
  { lat: 22.6908, lng: 86.5150 },
  { lat: 23.3169, lng: 85.8747 },
  { lat: 23.6617, lng: 86.6256 },
  { lat: 24.5783, lng: 87.8997 },
  { lat: 25.1028, lng: 87.7900 },
  { lat: 25.1114, lng: 87.2681 },
  { lat: 24.8217, lng: 85.7231 },
  { lat: 24.5261, lng: 83.4228 }, // Closing the polygon
];

// Coordinates for Ranchi
const ranchiCoords = [
  { lat: 23.3697, lng: 85.2203 },
  { lat: 23.3181, lng: 85.2467 },
  { lat: 23.2417, lng: 85.3036 },
  { lat: 23.3111, lng: 85.3928 },
  { lat: 23.3886, lng: 85.4356 },
  { lat: 23.4058, lng: 85.4153 },
  { lat: 23.4394, lng: 85.3589 },
  { lat: 23.4481, lng: 85.3019 },
  { lat: 23.4058, lng: 85.2614 },
  { lat: 23.4281, lng: 85.2169 },
];

// Coordinates for other polygons
const redPolygonCoords = [
    { lat: 23.4525, lng: 85.4569 },
    { lat: 23.4453, lng: 85.4525 },
    { lat: 23.4382, lng: 85.4620 },
    { lat: 23.4284, lng: 85.4708 },
    { lat: 23.4289, lng: 85.4739 },
    { lat: 23.4372, lng: 85.4738 },
    { lat: 23.4460, lng: 85.4800 },
    { lat: 23.4500, lng: 85.4713 },
    { lat: 23.4515, lng: 85.4703 },
    { lat: 23.4512, lng: 85.4621 }, // Closing the array properly
];

const newPolygonCoords = [
  { lat: 23.4908, lng: 85.4683 },
  { lat: 23.4836, lng: 85.4647 },
  { lat: 23.4794, lng: 85.4678 },
  { lat: 23.4769, lng: 85.4725 },
  { lat: 23.4758, lng: 85.4803 },
  { lat: 23.4800, lng: 85.4850 },
  { lat: 23.4819, lng: 85.4872 },
  { lat: 23.4911, lng: 85.4889 },
  { lat: 23.4900, lng: 85.4753 },
  { lat: 23.4917, lng: 85.4686 }, // Closing the polygon
];

const MapComponent = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyChN074R5gt4BfPTFox9UXnND0S8yyH6Dk", // replace with your actual Google API key
  });

  const [showJharkhandPolygon, setShowJharkhandPolygon] = useState(true);
  const [showRanchiPolygon, setShowRanchiPolygon] = useState(true);
  const [showNewPolygon, setShowNewPolygon] = useState(true);
  const [showRedPolygon, setShowRedPolygon] = useState(true);

  const handlePolygonClick = (setShowPolygon) => {
    setShowPolygon(false); // Hide the polygon on click
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={zoomLevel}
    >
      {showJharkhandPolygon && (
        <PolygonF
          paths={jharkhandCoords}
          options={{ fillColor: '#00FF00', strokeColor: '#000000' }}
          onClick={() => handlePolygonClick(setShowJharkhandPolygon)}
        />
      )}

      {showRanchiPolygon && (
        <PolygonF
          paths={ranchiCoords}
          options={{ fillColor: '#FF0000', strokeColor: '#000000' }}
          onClick={() => handlePolygonClick(setShowRanchiPolygon)}
        />
      )}

      {showNewPolygon && (
        <PolygonF
          paths={newPolygonCoords}
          options={{ fillColor: '#FFA500', strokeColor: '#000000' }}
          onClick={() => handlePolygonClick(setShowNewPolygon)}
        />
      )}

      {showRedPolygon && (
        <PolygonF
          paths={redPolygonCoords}
          options={{ fillColor: '#FF0000', strokeColor: '#000000' }}
          onClick={() => handlePolygonClick(setShowRedPolygon)}
        />
      )}
    </GoogleMap>
  ) : <></>;
};

export default MapComponent;
